import {useState ,React} from 'react'
import './login.css'
import axios from 'axios';
import Home from './home'
export default function Login  ()  {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');


    async function llogin() {
        console.warn(email, password);
    
        try {
          let result = await axios.post(
            'http://localhost:8081/check',
            {
              email: email,
              password: password,
            },
            {
              headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
              },
            }
          );
    
          
    
          if (result.data === '1') {
            // Redirect to Home component
             <Home />
            // history.push("/home"); 
            // Adjust the path to match your route
        
          } else {
            // Set login error
            setLoginError('Incorrect email or password');
          }
        } catch (error) {
          console.error('Error making the request:', error);
          // Handle error
          setLoginError('An error occurred during login');
        }
      }
      const handleSubmit = (e) => {
        e.preventDefault();
        llogin();
      };
  return (
    <div className='mp'>
    <div className="auth-form-container">
        <h2>Login</h2>
        <form className="login-form" onSubmit={handleSubmit}>
            <label htmlFor="roll_number">Roll Number </label>
            <input value={email} onChange={(e) => setEmail(e.target.value)} type="roll_number" placeholder="roll_number" id="roll_number" name="roll_number" />
            <label htmlFor="password">password </label>
            <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="********" id="password" name="password" />
            <button type="submit">Log In</button>
        </form>
        {/* <button className="link-btn">Don't have an account? Register here.</button> */}
    </div>
</div>
  )
}
